/*
Translating RNA into Protein
https://rosalind.info/problems/prot/
*/

use phf::phf_map;

fn translate_rna_to_protein(rna: &str) -> Vec<String> {
    let chars = rna.chars().collect::<Vec<char>>();
    let mapped: String = chars.chunks(3)
        .map(|group| {
            let rna_codon: String = group.iter().collect();
            CODON_MAP.get(rna_codon.as_str()).unwrap()
        })
        .collect();
    mapped.split('*').map(|s| s.to_string()).collect()
}


#[cfg(test)]
mod tests {
    use crate::prot::translate_rna_to_protein;

    #[test]
    fn it_works() {
        let sample = "AUGGCCAUGGCGCCCAGAACUGAGAUCAAUAGUACCCGUAUUAACGGGUGA";
        let expected = "MAMAPRTEINSTRING";
        assert_eq!(expected, translate_rna_to_protein(sample).get(0).unwrap());
    }

    #[test]
    fn actual_dataset() {
        let sample = "";
        println!("{:?}", translate_rna_to_protein(sample));
        // Correct answer:
        // 
    }
}

